                      <NavLink to={`#${item.id}`} className="w-40 w-sm-100">
                        <p className="list-item-heading mb-1 color-theme-1">{item.title}</p>
                        <p className="mb-1 text-muted text-small">Products | {item.category}</p>
                        <p className="mb-4 text-small">{item.description}</p>
                      </NavLink>
                      {items.length !== i + 1 && <Separator />}
                    </div>
                  );
                })
              }
      </div>
    )
  }
}



const mapStateToProps = (state) => {
  return {
    cardData: state.card.cardData,
  }
}
export default connect(mapStateToProps, { getCards })(App);

